package solutions;

public class SolutionFor8 implements Solution {

	@Override
	public void solve() {
		String s = "73167176531330624919225"
				+ "119674426574742355349194"
				+ "934969835203127745063262395783180169848"
				+ "";

		long maxProd=-1;
		long runningProd=1;
		int runningCount=0;
		for (int i=0; i<s.length(); i++){
			
			runningCount++;
			
			runningProd*=s.charAt(i) - '0';
			
			System.out.println("at " + i + "; count " + runningCount + " " + runningProd);
			
			if(runningProd==0) {
				runningCount=0;
				runningProd=1;
			}
			
			if(runningCount>13){
				runningProd/=s.charAt(i-13)-'0';
				runningCount--;
			}
			
			if(runningCount==13 && maxProd<runningProd){
				maxProd=runningProd;
			}
			
		}
		System.out.println(maxProd);
	}

}
